#!/usr/bin/env python3

s1 = 'SHINCHAN'
s2 = 'NOHARAAA'

s1 = ''

s2 = ''


def get_common_s12(s1, s2):
    if not s1 or not s2:
        return [], []

    new_s1 = None
    common_s = set(s1) & set(s2)
    if not common_s:
        return [], []

    new_s1 = [x for x in list(s1) if x in common_s]
    new_s2 = [x for x in list(s2) if x in common_s]

    return new_s1, new_s2


def find_c(c, s2):
    num = 0
    while(num < len(s2)):
        if s2[num] == c:
            return num

        num += 1

    return -1


def get_max_intersection_number(s1, s2):
    print("input [{}], [{}]".format(s1, s2))
    n = 0
    num_list = []
    while(n < len(s1)):
        pos = find_c(s1[n], s2)
        if pos == -1:
            break

        ns1 = s1[n + 1:]
        ns2 = s2[pos + 1:]

        ns1, ns2 = get_common_s12(ns1, ns2)
        if not ns1 or not ns2:
            break

        max_num = get_max_intersection_number(ns1, ns2) + 1
        num_list.append(max_num)

        n += 1

    return max(num_list) if num_list else 0


s1, s2 = get_common_s12(s1, s2)
n = get_max_intersection_number(s1, s2)
print(n)
