#!/usr/bin/env python3

# Common Child

import itertools as it

s1 = 'HARRYXBYHZA'
s2 = 'YASALLYZTA'


def find_sub_string(s1, s2):
    '''
    s2 as substring
    '''
    max_num = 0
    pos = s1.find(s2[0])
    if pos >= 0:
        try:
            num = find_sub_string(s1[pos:], s2[1:])
        except IndexError:
            num = 0

        max_num = max_num + num
    else:
        return 0

    return max_num

# step 1, get unintersection characters as a set


def find_str(s1, s2):
    '''
    Check 's2' is in s1 or not
    Return True if it is in s
    '''
    for i in range(len(s2)):
        pos = s1.find(s2[i])
        if pos >= 0:
            s1 = s1[pos + 1:]
        else:
            return False

    return True


def commonChild(s1, s2):
    s1_set = set(s1)
    s2_set = set(s2)

    common_characters = sorted((set(s1) & set(s2)))

    # step 2, adjust the s1 to be only contains common characters

    new_s1 = []
    new_s2 = []

    [new_s1.append(x) for x in s1 if x in common_characters]
    new_s1 = ''.join(new_s1)

    # step 3, adjust the s2 to be only contains common characters

    [new_s2.append(x) for x in s2 if x in common_characters]
    new_s2 = ''.join(new_s2)

    '''
    step 4, get a sub string with length x( x from n-1 to 0 ) in s1,
    find the sub string in s2, if find, return the x
    '''
    bfound = False
    for x in range(len(new_s1), 0, -1):
        # print(new_s1[0:x])
        for sub_set in it.combinations(new_s1, x):
            if find_str(new_s2, sub_set) == True:
                return len(sub_set)

    return 0


s1 = 'abyelwjvsxed'
s2 = 'hgygsvlfwcwnswtuhmyaljkqlqjjqlqkjlaymhutwsnwcflvsgygh'

s1 = ''

s2 = ''

ret = commonChild(s1, s2)
print(ret)
